
package JavaBasic;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    System.out.println("hello");
	    
	    
	    //Primitive data types = int,double,boolean,char
	    
	    // Integer data type
	    
	    int i = 10;
	    i = 20;
	    System.out.println(i);
	    
	    // Double data type
	    
	    double d = 23.13;
	    double d1 = 32.33;
	    System.out.println(d1);
	    
	    //char data type can not have more than 1 character 
	    
	    char a = 'a';
	    char b = 'b';
	    System.out.println(b);
	    System.out.println("laxminarayan");
	    
	    // boolean data type either true or false
	    
	    boolean k= true;
	    boolean p= false;
	    
	    //string is a class not a data type, string is written in double quote
	   
	    String s = "laxminaryan";
	    String N = "Gaurav";
	    String s1 = "1000";
	    String S2 = "10.10";
	    System.out.println(S2);
	    
	    
	    
	    

	}

}
